UPDATE npc_text SET text0_0='你好。我是一个亡灵法师训练师，而你是一个法师。',text0_1='你好。我是一个亡灵法师训练师，而你是一个法师。' WHERE ID=562 ;
UPDATE npc_text SET text0_0='你好，$c。我给你的建议是：当你在世界各地旅行的时候，要当心魔法，它会将不熟悉它的人烧成灰。',text0_1='你好，$c。我给你的建议是：当你在世界各地旅行的时候，要当心魔法，它会将不熟悉它的人烧成灰。' WHERE ID=563 ;
UPDATE npc_text SET text0_0='你好, $c.  你是来这里学习黑暗魔法的吗？',text0_1='你好, $c.  你是来这里学习黑暗魔法的吗？' WHERE ID=564 ;
UPDATE npc_text SET text0_0='$c， 你这个可怜的家伙。奥术的力量远远超出你的想象...',text0_1='$c， 你这个可怜的家伙。奥术的力量远远超出你的想象...' WHERE ID=565 ;
UPDATE npc_text SET text0_0='现在还不是让你长眠的时候。我会帮你起死回生……但你也要为此付出一些代价。',text0_1='现在还不是让你长眠的时候。我会帮你起死回生……但你也要为此付出一些代价。' WHERE ID=580 ;
