UPDATE npc_text SET text0_0='再次感谢你帮助希拉里，$g先生:小姐;!我长大了也要成为你这样的英雄!',text0_1='再次感谢你帮助希拉里，$g先生:小姐;!我长大了也要成为你这样的英雄!' WHERE ID=2277 ;
