UPDATE npc_text SET text0_0='洛丹伦废墟以北建筑顶端那有地精。离开幽暗城，前往那儿吧。',text0_1='洛丹伦废墟以北建筑顶端那有地精。离开幽暗城，前往那儿吧。' WHERE ID=3520 ;
UPDATE npc_text SET text0_0='牧师训练者？去军事区的外圈找找看。',text0_1='牧师训练者？去军事区的外圈找找看。' WHERE ID=3521 ;
UPDATE npc_text SET text0_0='拿到那颗头颅肯定会让达瑞尔大吃一惊。是他活该，这个傲慢又该死的混蛋。但是哈!我等不及想看到你把这个拿给他看时他脸上的表情。',text0_1='拿到那颗头颅肯定会让达瑞尔大吃一惊。是他活该，这个傲慢又该死的混蛋。但是哈!我等不及想看到你把这个拿给他看时他脸上的表情。' WHERE ID=3522 ;
