UPDATE npc_text SET text0_0='你在这里永远都受欢迎，$N。谢谢你带来的欢笑--还有不要担心达瑞尔，他就是这样一个人!',text0_1='你在这里永远都受欢迎，$N。谢谢你带来的欢笑--还有不要担心达瑞尔，他就是这样一个人!' WHERE ID=3525 ;
