UPDATE npc_text SET text0_0='愿圣光与你同在，$c。我能帮上忙吗?',text0_1='愿圣光与你同在，$c。我能帮上忙吗?' WHERE ID=3974 ;
UPDATE npc_text SET text0_0='你可能不会像一个银手骑士那样追寻光之踪迹，但是你总可以向我们寻求帮助。',text0_1='你可能不会像一个银手骑士那样追寻圣光的踪迹，但是你总可以向我们寻求帮助。' WHERE ID=3975 ;
