UPDATE npc_text SET text0_0='既然战火蔓延已达巅峰，到目前为止，部落与联盟的强壮战士都没有短缺。$b$b但愿我们能将他们赶尽杀绝……',text0_1='既然战火蔓延已达巅峰，到目前为止，部落与联盟的强壮战士都没有短缺。$b$b但愿我们能将他们赶尽杀绝……' WHERE ID=8114 ;
