UPDATE npc_text SET text0_0='陶土议会想要保存元素的力量和庄严。',text0_1='陶土议会想要保存元素的力量和庄严。' WHERE ID=9181 ;
UPDATE npc_text SET text0_0='元素这样混乱要我的人民怎么作战?他们难道看不出来没有元素的支持这世界无法生存吗。' WHERE ID=9182 ;
